mapper.select:
SELECT oo.id, oo.customer_id, oo.order_sn, oo.order_status, oo.pull_status, oo.order_from, oo.ec_platform_code, oo.reference_sn, oo.store_id, oo.wh_id, oo.pull_operator_id, oo.pack_operator_id, oo.out_gw, oo.out_volume, oo.remark, oo.is_wrong, oo.cr_time, oo.inner_org_id FROM out_order as oo LEFT JOIN out_order_commodity_item ooci ON oo.id = ooci.order_id
oo.is_wrong = #{isWrong}
and oo.cr_time between #{kd.startStamp} and #{kd.endStamp}
oo.order_status = #{kd.orderStatus}
and oo.wh_id = #{kd.whId}
and oo.customer_id = #{kd.customerId}
and locate(#{kd.orderSn} , oo.order_sn) > 0
and locate(#{kd.commodityName} , ooci.commodity_sku) > 0
and locate(#{kd.commodityName} , ooci.commodity_name) > 0
order by oo.id desc

SELECT DISTINCT oo.id, oo.customer_id, oo.order_sn, oo.order_status, oo.pull_status, oo.order_from, oo.ec_platform_code, oo.reference_sn, oo.store_id, oo.wh_id, oo.pull_operator_id, oo.pack_operator_id, oo.out_gw, oo.out_volume, oo.remark, oo.is_wrong, oo.cr_time FROM out_order as oo LEFT JOIN out_order_commodity_item ooci ON oo.id = ooci.order_id
oo.order_status = 3
and oo.pull_status = #{kd.pullStatus}
and oo.wh_id = #{kd.whId}
and oo.customer_id = #{kd.customerId}
and oo.pull_operator_id = #{kd.pullOperatorId}
and locate(#{kd.orderSn} , oo.order_sn) > 0
and locate(#{kd.commoditySku} , ooci.commodity_sku) > 0
and locate(#{kd.commodityName} , ooci.commodity_name) > 0
order by oo.id desc

