mapper.select:
SELECT id, customer_id, order_sn, order_status, pull_status, order_from, ec_platform_code, reference_sn, store_id, wh_id, remark, cr_time FROM out_order as o where 1=1
AND o.id=${ids}
AND o.id BETWEEN ${minId} AND ${maxId}
AND o.id in (${ids}) order by o.id desc

mapper.update:
update  out_order  set order_status=#{status}
where id=#{id}

    AND customer_id =  #{customerId}

update  out_order  set pull_status=#{status}
where

    #{orderId}


    AND customer_id =  #{customerId}

mapper.select:
